package main

import (
	"fmt"
	"os"
	"strings"

	. "github.com/dave/jennifer/jen"
	"golang.org/x/net/html"
	"mvdan.cc/gofumpt/format"
)

func Traverse(doc *html.Node) *Statement {
	var crawler func(*html.Node) *Statement
	crawler = func(node *html.Node) *Statement {
		el := Null()

		if node.Type == html.TextNode && strings.TrimSpace(node.Data) != "" {
			el = Qual(pkg, "Text").Call(Lit(node.Data))
		} else if node.Type == html.ElementNode && node.DataAtom.String() != "" {
			el = Qual(pkg, goifyTag(node.DataAtom.String())).Call()
			for _, attr := range node.Attr {
				if strings.Contains(attr.Key, "-") {
					parts := strings.Split(attr.Key, "-")

					el.Dot(goifyTag(parts[0])).Call(Lit(goifyTag(strings.Join(parts[1:], "-"))), Lit(attr.Val))
				} else {
					el.Dot(goifyTag(attr.Key)).Call(Lit(attr.Val))
				}
			}
		}

		children := []Code{}
		for child := node.FirstChild; child != nil; child = child.NextSibling {
			children = append(children, crawler(child))
		}

		if len(children) > 0 {
			el.Dot("Body").Call(children...)
		}

		return el
	}

	return crawler(doc)
}

func goifyTag(tag string) string {
	return strings.Join(strings.Fields(strings.Title(strings.ReplaceAll(tag, "-", " "))), "")
}

func main() {
	source := NewFile("components")

	source.Type().Id(component).Struct(
		Qual(pkg, "Compo"),
	)

	render := source.Func().
		Params(
			Id("c").Id("*" + component),
		).
		Id("Render").
		Params().
		Params(Qual(pkg, "UI"))

	root, err := html.Parse(strings.NewReader(input))
	if err != nil {
		panic(err)
	}

	parsed := Traverse(root)

	render.Block(Return(
		Qual(pkg, "Div").Call().Add(parsed),
	))

	// fmt.Printf("%#v", source)

	if err := os.Setenv("GOFUMPT_SPLIT_LONG_LINES", "on"); err != nil {
		panic(err)
	}

	out, err := format.Source([]byte(fmt.Sprintf("%#v", source)), format.Options{})
	if err != nil {
		panic(err)
	}

	fmt.Println(string(out))
}

